mapper.select:
SELECT article_id , group_id , sequence_no , posting_date, read_count, writer_name , password , title
FROM (SELECT article_id , group_id , sequence_no , posting_date, read_count, writer_name , password , title, ROW_NUMBER() OVER (ORDER BY sequence_no DESC) R FROM freeboard) 
WHERE R BETWEEN #{firstRow} and #{endRow}

SELECT read_count
FROM freeboard
WHERE article_id = #{articleId}

SELECT MIN(sequence_no)
FROM freeboard
WHERE sequence_no < #{maxNumber} AND sequence_no >= #{minNumber}

mapper.update:
UPDATE freeboard
SET read_count = #{readCount}
WHERE article_id = #{articleId}
